public static void showNoData(View view, int visibility) {
    if (view != null) {
        view.setVisibility(visibility);
        if(visibility == View.VISIBLE) {
            TextView tvNoData = view.findViewById(R.id.tv_no_data);
            if (view.findViewById(R.id.player_progressbar) != null) {
                view.findViewById(R.id.player_progressbar).setVisibility(View.GONE);
            }
            if (tvNoData != null) {
                tvNoData.setVisibility(View.VISIBLE);
                if (SupportUtil.isNotConnected(view.getContext())) {
                    tvNoData.setText(AppConstant.NO_INTERNET_CONNECTION);
                } else {
                    tvNoData.setText(AppConstant.NO_DATA);
                }
            }
        }
    }
}

public class SectionsPagerAdapter extends FragmentPagerAdapter {
    private final List<Fragment> mFragmentList = new ArrayList<>();
    private final List<String> mFragmentTitleList = new ArrayList<>();

    public SectionsPagerAdapter(FragmentManager fm) {
        super(fm, BEHAVIOR_RESUME_ONLY_CURRENT_FRAGMENT);
    }

    public void addList(List<ExamModel> classModels) {
        removePreviousData();
        for (ExamModel classModel : classModels) {
            Fragment fragment = StudyUtil.getFragmentByType(classModel);
            addFrag(fragment, classModel.getName());
        }
    }

    private void removePreviousData(){
        mFragmentList.clear();
        mFragmentTitleList.clear();
    }

    private void addFrag(Fragment fragment, String title) {
        mFragmentList.add(fragment);
        mFragmentTitleList.add(title);
    }

    @Override
    @NonNull
    public Fragment getItem(int position) {
        return mFragmentList.get(position);
    }

    @Override
    public int getCount() {
        return mFragmentList.size();
    }


    @Override
    public CharSequence getPageTitle(int position) {
        return mFragmentTitleList.get(position);
    }

}

public static String getImagePath(String imageBaseUrl, String photoUrl) {
    String imageUrl = "";
    if (!TextUtils.isEmpty(photoUrl)) {
        if (imageBaseUrl != null && !photoUrl.startsWith("file://") && !photoUrl.startsWith("http://") && !photoUrl.startsWith("https://")) {
            imageUrl = imageBaseUrl + photoUrl;
        } else {
            imageUrl = photoUrl;
        }
    }
    return imageUrl;
}

import android.view.LayoutInflater;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.helper.model.HistoryModelResponse;

import java.util.ArrayList;

public class VideoListAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder> {
    private final int mItemType;
    private ArrayList<HistoryModelResponse> mList;

    public VideoListAdapter(int mItemType, ArrayList<HistoryModelResponse> mList) {
        this.mItemType = mItemType;
        this.mList = mList;
    }

    public interface ItemType {
        int ITEM_TYPE_GRID = 1;
        int ITEM_TYPE_SCROLL = 0;
    }

    @Override
    public int getItemViewType(int position) {
        return mItemType;
    }

    @NonNull
    @Override
    public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {
        if (i == SubCatAdapter.ItemType.ITEM_TYPE_GRID) {
            return new GridViewHolder(LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.slot_video_grid, viewGroup, false));
        } else {
            return new ScrollViewHolder(LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.slot_video_scroll, viewGroup, false));
        }
    }

    @Override
    public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, final int position) {
        HistoryModelResponse item = mList.get(position);
        if (holder instanceof ScrollViewHolder) {
            ScrollViewHolder viewHolder = (ScrollViewHolder) holder;
            viewHolder.tvName.setText(item.getName());
        }else if (holder instanceof GridViewHolder) {
            GridViewHolder viewHolder = (GridViewHolder) holder;
            try {
                if(item.getName().contains(" ")){
                    String[] mData = item.getName().split(" ");
                    viewHolder.tvHeader.setText(mData[0]);
                    viewHolder.tvName.setText(mData[1]);
                }else {
                    viewHolder.tvName.setText(item.getName());
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        }
    }

    @Override
    public int getItemCount() {
        return mList.size();
    }

    public static class ScrollViewHolder extends RecyclerView.ViewHolder {
        private TextView tvName;

        ScrollViewHolder(View view) {
            super(view);
            tvName = view.findViewById(R.id.tv_name);
        }
    }

    public static class GridViewHolder extends RecyclerView.ViewHolder {
        private TextView tvName;
        private TextView tvHeader;

        GridViewHolder(View view) {
            super(view);
            tvHeader = view.findViewById(R.id.tv_header);
            tvName = view.findViewById(R.id.tv_name);
        }
    }
}

import android.os.Bundle;
import android.os.Handler;
import android.text.TextUtils;
import android.view.MenuItem; 
import androidx.appcompat.app.ActionBar;
import androidx.appcompat.app.AppCompatActivity;
import androidx.fragment.app.Fragment;
import androidx.fragment.app.FragmentTransaction; 


public class FragmentHolderActivity extends AppCompatActivity {

    private String title;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_frag_holder);
        initFragment();
    }

    private void initFragment() {
        final Bundle bundle = getIntent().getExtras();
        if (bundle != null) {
            title = bundle.getString(AppConstant.TITLE);

            Runnable runnable = new Runnable() {
                public void run() {
                    Fragment fragment = new FragmentHolderFragment();
                    fragment.setArguments(bundle);
                    FragmentTransaction transaction = getSupportFragmentManager().beginTransaction();
                    transaction.add(R.id.content, fragment);
                    transaction.commitAllowingStateLoss();
                }
            };
            new Handler().post(runnable);
            setUpToolBar();
        } else {
            finish();
        }
    }

    private void setUpToolBar() {
        ActionBar actionBar = getSupportActionBar();
        if (actionBar != null) {
            actionBar.setDisplayHomeAsUpEnabled(true);
            if (!TextUtils.isEmpty(title)) {
                actionBar.setTitle(title);
            }
        }
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        int id = item.getItemId();
        if( id == android.R.id.home ){
            onBackPressed();
            return true ;
        }
        return super.onOptionsItemSelected(item);
    } 
}

