public class ExamClassSelector implements Response.OnClickListener<String> { 
    private BottomSheetDialog mBottomSheetDialog;
    private ExamModel mItem;
    private BoardSelectionType boardSelectionType;

    public static ExamClassSelector newInstance(ExamModel mItem, BoardSelectionType boardSelectionType, Study.OnClickListener<ExamModel> mListener) {
        ExamClassSelector fragment = new ExamClassSelector();
        fragment.mItem = mItem;
        fragment.boardSelectionType = boardSelectionType;
        fragment.mListener = mListener;
        return fragment;
    }

    public void show(Context context) {
        if (context != null) {
            mBottomSheetDialog = new BottomSheetDialog(context, R.style.BottomSheetDialog);
            LayoutInflater inflater = LayoutInflater.from(context);
            mBottomSheetDialog.setContentView(viewHolder(inflater.inflate(R.layout.study_fragment_class_selection, null)));
            mBottomSheetDialog.show();
        }
    }

    private View viewHolder(View v) {
        RecyclerView mRecyclerView = v.findViewById(R.id.recycler_view);
        View llNoData = v.findViewById(R.id.ll_no_data);
        GridLayoutManager horizontalManager = new GridLayoutManager(v.getContext(), 2);

        mRecyclerView.setLayoutManager(horizontalManager);
        mRecyclerView.setItemAnimator(new DefaultItemAnimator());
        if (mItem != null) {
            if (mItem.getChildren() != null && mItem.getChildren().size() > 0) {
                List<ExamModel> mList = mItem.getChildren();
                ExamClassSelectionAdapter adapter = new ExamClassSelectionAdapter(mList, this);
                mRecyclerView.setAdapter(adapter);
                StudyUtil.showNoData(llNoData, View.GONE);
            } else {
                StudyUtil.showNoData(llNoData, View.VISIBLE);
            }
        }
        v.findViewById(R.id.ll_change_board).setOnClickListener(view -> {
            if (mBottomSheetDialog != null) {
                mBottomSheetDialog.dismiss();
            }
        });
        return v;
    }

    @Override
    public void onItemClicked(View view, ExamModel item) {
        if (mBottomSheetDialog != null) {
            mBottomSheetDialog.dismiss();
        }
        mListener.onItemClicked(view, item);
        mBottomSheetDialog = null;
    }

}

