import android.text.TextUtils;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.pdfviewer.R;
import com.pdfviewer.util.PDFCallback;

import java.util.List;

public class PDFIndexAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder> {

    private final PDFCallback.OnListClickListener<PDFModel> clickListener;
    private List<PDFModel> mList;

    @Override
    @NonNull
    public RecyclerView.ViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {
        return new ViewHolder(LayoutInflater.from(parent.getContext())
                .inflate(R.layout.mcq_item_performance_test, parent, false));
    }

    @Override
    public int getItemCount() {
        return mList == null ? 0 : mList.size();
    }

    public PDFIndexAdapter(List<PDFModel> mList, PDFCallback.OnListClickListener<PDFModel> clickListener) {
        this.mList = mList;
        this.clickListener = clickListener;
    }

    @Override
    public void onBindViewHolder(@NonNull final RecyclerView.ViewHolder holder, int i) {
        ViewHolder myViewHolder = (ViewHolder) holder;
        myViewHolder.tvTitle.setText(mList.get(i).getTitle());
        if(!TextUtils.isEmpty(mList.get(i).getBookmarkPages())) {
            myViewHolder.tvPages.setText("Pages : " + mList.get(i).getBookmarkPages());
        }

        if(!TextUtils.isEmpty(mList.get(i).getSubTitle())) {
            myViewHolder.tvSubTitle.setText(mList.get(i).getSubTitle());
            myViewHolder.tvSubTitle.setVisibility(View.VISIBLE);
        }else {
            myViewHolder.tvSubTitle.setVisibility(View.GONE);
        }
    }

    private class ViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {

        private TextView tvTitle;
        private TextView tvSubTitle;
        private TextView tvPages;

        private ViewHolder(View v) {
            super(v);
            tvTitle = v.findViewById(R.id.tvTitle);
            tvSubTitle = v.findViewById(R.id.tvSubTitle);
            tvPages = v.findViewById(R.id.tvPages);

            itemView.setOnClickListener(this);
            v.findViewById(R.id.iv_delete).setOnClickListener(this);
        }

        @Override
        public void onClick(View v) {
            if (getAdapterPosition() >= 0 && getAdapterPosition() < mList.size()) {
                if (v.getId() == R.id.iv_delete) {
                    clickListener.onDeleteClicked(v, getAdapterPosition(), mList.get(getAdapterPosition()));
                } else {
                    clickListener.onItemClicked(v, mList.get(getAdapterPosition()));
                }
            }
        }
    }

}

public void insertDAILYUPDATES(List<DailyUpdatesModel> dailyUpdatesModels) {
        if (!db.isOpen())
            db = openDataBase();
        try {
            for (DailyUpdatesModel dailyUpdatesModel : dailyUpdatesModels) {
                ContentValues contentValues = new ContentValues();
                contentValues.put(ID, dailyUpdatesModel.getId());
                contentValues.put(COLUMN_STATUS, dailyUpdatesModel.getCategory_id());
                contentValues.put(COLUMN_TITLE, "" + dailyUpdatesModel.getTitle());
                contentValues.put(COLUMN_DES, "" + dailyUpdatesModel.getDescription());
                contentValues.put(COLUMN_UPDATE_AT, "" + dailyUpdatesModel.getUpdatedAt());
                contentValues.put(COLUMN_IMAGE_PATH, "" + dailyUpdatesModel.getImage());
                int c = db.update(TABLE_DAILY_UPDATES, contentValues, ID + "=" + dailyUpdatesModel.getId(), null);
                if (c == 0)
                    db.insert(TABLE_DAILY_UPDATES, null, contentValues);
            }

        } catch (Exception e) {
            Log.e(TAG, "" + e);
        }
    }

