public class ReflectionClass<T> {

    public MenuListener<T> mListener;
    public T item;

    public interface MenuListener<T> {
        void onClick(T item);
    }

    public static <T> ReflectionClass<T> newInstance(MenuListener<T> listener, T itemProduct) {
        ReflectionClass<T> reflectionClass = new ReflectionClass<T>();
        reflectionClass.mListener = listener;
        reflectionClass.item = itemProduct;
        return reflectionClass;
    }

    public void onViewCreated(@NonNull View v) {
        TextView tvTitle = v.findViewById(R.id.action_menu_title);

        if (item != null && item instanceof ProductModel) {
            tvTitle.setText(((ProductModel) item).getProductName());
        }
        tvTitle.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                if (mListener != null) {
                    mListener.onClick(item);
                }
            }
        });
    }
}

