private final ArrayList<PDFCallback.StatsListener> mStatsCallbacks = new ArrayList<>();

public PDFViewer addStatisticsCallbacks(PDFCallback.StatsListener callback) {
    synchronized (mStatsCallbacks) {
        mStatsCallbacks.add(callback);
    }
    return this;
}

public void removeStatisticsCallbacks(PDFCallback.StatsListener callback) {
    synchronized (mStatsCallbacks) {
        mStatsCallbacks.remove(callback);
    }
}

public void dispatchStatsUpdated() {
    for (PDFCallback.StatsListener callback : mStatsCallbacks) {
        callback.onStatsUpdated();
    }
}

public static void showKeyboard(View view) {
    try {
        if (view != null && view.requestFocus()) {
            InputMethodManager imm = (InputMethodManager)
                    view.getContext().getSystemService(Context.INPUT_METHOD_SERVICE);
            if(imm!=null) {
                imm.toggleSoftInput(InputMethodManager.SHOW_FORCED, 0);
            }
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
}
public static void hideKeyboard(Activity activity) {
    try {
        if(activity != null) {
            InputMethodManager imm = (InputMethodManager) activity.getSystemService(Context.INPUT_METHOD_SERVICE);
            View f = activity.getCurrentFocus();
            if (null != f && null != f.getWindowToken() && EditText.class.isAssignableFrom(f.getClass()))
                imm.hideSoftInputFromWindow(f.getWindowToken(), 0);
            else
                activity.getWindow().setSoftInputMode(WindowManager.LayoutParams.SOFT_INPUT_STATE_ALWAYS_HIDDEN);
        }
    } catch (Exception e) {
        e.printStackTrace();
    }
}

private void loadDataFromServer() {
	if (!MCQUtil.isConnected(activity)) {
		MCQUtil.showNoData(viewNoData, View.VISIBLE);
		if (MCQSdk.getInstance().getConfigManager() != null)
			MCQSdk.getInstance().getConfigManager().getNetworkMonitor().setConnectivityListener(this.hashCode(), new ConnectivityListener() {
				@Override
				public void onNetworkStateChanged(boolean isConfigLoaded, boolean isConnected) {
					if (isConfigLoaded && isConnected) {
						loadDataFromServer();
					}
				}
			});
	} else {
		networkUtil.fetchUserPerformanceData(userId);
	}
}

@Override
public void onDestroy() {
	super.onDestroy();
	if (MCQSdk.getInstance().getConfigManager() != null) {
		MCQSdk.getInstance().getConfigManager().getNetworkMonitor().removeConnectivityListener(this.hashCode());
	}
}

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Filter;
import android.widget.Filterable;
import android.widget.TextView;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.helper.callback.Response;

import java.util.ArrayList;
import java.util.List;

public class FilterAdapter extends RecyclerView.Adapter<RecyclerView.ViewHolder> implements Filterable {
    private final List<FilterModel> mList;
    private final Response.OnClickListener<FilterModel> mCallback;
    public final ArrayList<FilterModel> tempList;

    public FilterAdapter(List<FilterModel> mList, Response.OnClickListener<FilterModel> mCallback) {
        this.mList = mList;
        this.mCallback = mCallback;
        this.tempList = new ArrayList<>(mList);
    }

    /**
     * @param list : update this list when new items added in main list.
     */
    public void updateFilterList(List<HistoryModelResponse> list) {
        tempList.clear();
        tempList.addAll(list);
    }

    @NonNull
    @Override
    public RecyclerView.ViewHolder onCreateViewHolder(@NonNull ViewGroup viewGroup, int i) {
        return new ViewHolder(LayoutInflater.from(viewGroup.getContext()).inflate(R.layout.slot_filter, viewGroup, false));
    }

    @Override
    public void onBindViewHolder(@NonNull RecyclerView.ViewHolder holder, final int position) {
        FilterModel item = mList.get(position);
        if (holder instanceof ViewHolder) {
            ViewHolder viewHolder = (ViewHolder) holder;
            viewHolder.tvName.setText(item.getTitle());
        }
    }

    @Override
    public int getItemCount() {
        return mList.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {
        private final TextView tvName;

        ViewHolder(View view) {
            super(view);
            tvName = view.findViewById(R.id.tv_name);
            itemView.setOnClickListener(this);
        }

        @Override
        public void onClick(View v) {
            if(getAdapterPosition() >= 0 && getAdapterPosition() < mList.size()) {
                mCallback.onItemClicked(v, mList.get(getAdapterPosition()));
            }
        }
    }

    @Override
    public Filter getFilter() {
        return filter;
    }

    private final Filter filter = new Filter() {
        @Override
        protected FilterResults performFiltering(CharSequence constraint) {
            List<FilterModel> filteredList = new ArrayList<>();
            if (constraint == null || constraint.length() == 0) {
                filteredList.addAll(tempList);
            } else {
                String filterPattern = constraint.toString().toLowerCase().trim();
                for (FilterModel item : tempList) {
                    if (item.getTitle().toLowerCase().contains(filterPattern)) {
                        filteredList.add(item);
                    }
                }
            }
            FilterResults results = new FilterResults();
            results.values = filteredList;
            return results;
        }
        @Override
        protected void publishResults(CharSequence constraint, FilterResults results) {
            mList.clear();
            notifyDataSetChanged();
            if (results.values != null) {
                mList.addAll((List<FilterModel>) results.values);
                notifyDataSetChanged();
            }
            if(refreshDataListener != null){
                refreshDataListener.onListRefreshed(getItemCount());
            }
        }
    };

    private ListRefreshListener mRefreshListener;

    /**
     * @param mRefreshListener : used for update no data TextView
     */
    public void addListRefreshListener(ListRefreshListener mRefreshListener) {
        this.mRefreshListener = mRefreshListener;
    }

    public interface ListRefreshListener{
        void onListRefreshed(int itemCount);
    }
}
